@use "abstract/mixins" as *;
@use "base/colors" as c;

.btn {
  border: 0;
  border-radius: 5px;
  transition: background-color 0.7s ease-in-out;
  transition: color 0.7s ease-in-out;
  &:focus-visible {
    outline: none;
  }
  &:active {
    transform: translateY(4px);
    animation: keyDrop 0ms ease-in forwards;
  }
}

.btn--primary {
  font-size: 32px;
  background-color: var(--key-bg);
  color: var(--key-txt);
  box-shadow: 0 4px var(--key-shdw);
  &:focus-visible {
    color: var(--key-bg-toggle);
    box-shadow: 0 4px var(--key-bg-toggle);
  }
}

.btn--secondary {
  background-color: var(--key-bg-accent);
  box-shadow: 0 4px var(--key-shdw-accent);
  color: var(--key-txt-secondary);
}

.btn--accent {
  background-color: var(--key-bg-toggle);
  box-shadow: 0 4px var(--key-shdw-toggle);
  color: var(--key-txt-accent);
  &:focus-visible {
    color: var(--key-txt-accent);
    box-shadow: 0 4px var(--key-txt-accent);
  }
}

.btn--small {
  width: 60px;
  height: 60px;
  @include mq($width: $largescreen) {
    width: 110px;
    height: 60px;
  }
}

.btn--long {
  width: 136px;
  height: 60px;
  @include mq($width: $largescreen) {
    width: 244px;
    height: 60px;
  }
}

