@use "abstract/mixins" as *;
@use "base/colors" as c;

.calculatorKeys {
  &__container {
    transition: background-color 0.7s ease-in-out;
    display: grid;
    justify-content: center;
    @include container($width: calc(375px - 2em));
    grid-template-columns: repeat(4, 1fr);
    gap: 1em;
    grid-template-areas:
      "seven eight nine del"
      "four five six plus"
      "one two three minus"
      "decimal zero divide times"
      "reset reset equal equal";

    [value="1"] {
      grid-area: one;
    }

    [value="2"] {
      grid-area: two;
    }

    [value="3"] {
      grid-area: three;
    }

    [value="4"] {
      grid-area: four;
    }

    [value="5"] {
      grid-area: five;
    }

    [value="6"] {
      grid-area: six;
    }

    [value="7"] {
      grid-area: seven;
    }

    [value="8"] {
      grid-area: eight;
    }

    [value="9"] {
      grid-area: nine;
    }

    [value="0"] {
      grid-area: zero;
    }

    [value="."] {
      grid-area: decimal;
    }

    [value="del"] {
      grid-area: del;
    }

    [value="reset"] {
      grid-area: reset;
    }

    [value="+"] {
      grid-area: plus;
    }

    [value="-"] {
      grid-area: minus;
    }

    [value="*"] {
      grid-area: times;
    }

    [value="/"] {
      grid-area: divide;
    }

    [value="="] {
      grid-area: equal;
    }

    background-color: var(--toggle-bg);
    border-radius: 10px;
    padding: 1.75em;
    margin-bottom: 2em;

    @include mq($width: $largescreen) {
      @include container($width: 1440px * 0.4);
      gap: 1.5em;
      padding: 2em;
      margin-bottom: 4em;
    }
  }
}

