@use "abstract/mixins" as *;
@use "base/typography" as *;
@use "base/colors" as c;

.header {
  color: var(--txt);
  &__container {
    display: flex;
    align-items: flex-end;
    transition: background-color 0.7s ease-in-out;
    transition: color 0.7s ease-in-out;
    @include container($width: calc(375px - 2em));
    margin: 2em auto;
    @include mq($width: $largescreen) {
      @include container($width: 1440px * 0.4);
      margin: 4em auto 1em;
    }
  }
  &__title {
    font-size: 32px;
    margin-right: 120px;
    @include mq($width: $largescreen) {
      font-size: 40px;
      margin-right: 339px;
    }
  }
  &__switchContainer {
    display: flex;
    align-items: flex-end;
  }
  &__text {
    text-transform: uppercase;
    margin-right: 21px;
  }
  &__text,
  &__label {
    font-size: 16px;
    font-weight: $fw-semiBold;
  }
  &__themeLabel {
    padding: 0 10px;
  }
  &__themeLabel > * + * {
    margin-left: 12px;
  }
  &__themeToggle {
    display: flex;
    border-radius: 20px;
    background-color: var(--toggle-bg);
    height: 100%;
    width: 100%;
    padding: 5px;
  }
  &__themeToggle > * + * {
    margin-left: 5px;
  }
}

sources.sass\\components\\_screenDisplay.scss:
@use "abstract/mixins" as *;
@use "base/typography" as *;
@use "base/colors" as c;

.display {
  color: var(--txt); 
  &__container {
    transition: background-color 0.7s ease-in-out;
    transition: color 0.7s ease-in-out;
    @include container($width: 343px);
    background-color: var(--screen-bg);
    border-radius: 10px;
    text-align: right;
    height: 104px;
    padding: 14px 28px 14px 12px;
    margin-bottom: 24px;
    @include mq($width: $largescreen) {
      @include container($width: 1440px * 0.4);
      height: 116px;
      padding: 16px 32px;
      margin-bottom: 32px;
    }
  }
  &__previousOperand {
    font-size: 16px;
  }
  &__currentOperand {
    font-size: 24px;
    margin-top: 14px;
    @include mq($width: $largescreen) {
      font-size: 42px;
      margin-top: 8px;
    }
  }
}

sources.sass\\components\\_radioButton.scss:
@use "abstract/mixins" as *;
@use "base/colors" as c;

.customRadio {
  height: 16px;
  width: 16px;
  border-radius: 50%;
  background-color: var(--toggle-bg);
  cursor: pointer;
  &:hover {
    border: 1px solid var(--key-bg-toggle);
  }
  &::after {
    content: "";
    width: 100%;
    height: 100%;
    display: block;
    background-color: var(--key-bg-toggle);
    border-radius: 50%;
    transform: scale(0);
  }
}

.radioInput:checked + 
.customRadio::after {
  transform: scale(1);
}

.radioInput {
    display: none;
  }

